ʐZ<?php exit; ?>a:1:{s:7:"content";O:8:"stdClass":24:{s:2:"ID";i:1727;s:11:"post_author";s:1:"1";s:9:"post_date";s:19:"2016-08-05 11:09:15";s:13:"post_date_gmt";s:19:"2016-08-05 03:09:15";s:12:"post_content";s:802:"工信部网络安全管理局对虚拟运营商新入网电话用户实名登记工作进行抽查和暗访，对在网用户实名登记信息合规率进行数据抽测，并于4日公布结果。抽查发现，15家虚拟运营商在新用户实名登记方面存在违规。工信部称，对新入网用户实名登记工作采取暗访方式，共暗访了26家转售企业营销网点109个，发现存在违规行为的网点37个。其中，暗访实体营销网点50个，发现违规网点13个，暗访网络营销网点59个，发现违规网点24个。分享通信、巴士在线等15家转售企业被暗访网点存在违规行为。 <a href="http://tech.qq.com/a/20160805/003160.htm" target="_blank" rel="nofollow" data-stat-click="kuaixun.kuaixunlianjie.28.21299">[原文链接]</a>";s:10:"post_title";s:62:"工信部：15家虚拟运营商在实名制方面存在违规";s:12:"post_excerpt";s:0:"";s:11:"post_status";s:7:"publish";s:14:"comment_status";s:4:"open";s:11:"ping_status";s:6:"closed";s:13:"post_password";s:0:"";s:9:"post_name";s:182:"%e5%b7%a5%e4%bf%a1%e9%83%a8%ef%bc%9a15%e5%ae%b6%e8%99%9a%e6%8b%9f%e8%bf%90%e8%90%a5%e5%95%86%e5%9c%a8%e5%ae%9e%e5%90%8d%e5%88%b6%e6%96%b9%e9%9d%a2%e5%ad%98%e5%9c%a8%e8%bf%9d%e8%a7%84";s:7:"to_ping";s:0:"";s:6:"pinged";s:0:"";s:13:"post_modified";s:19:"2016-08-05 11:09:15";s:17:"post_modified_gmt";s:19:"2016-08-05 03:09:15";s:21:"post_content_filtered";s:0:"";s:11:"post_parent";i:0;s:4:"guid";s:45:"http://qisumu.com/?post_type=news&#038;p=1727";s:10:"menu_order";i:0;s:9:"post_type";s:4:"news";s:14:"post_mime_type";s:0:"";s:13:"comment_count";s:1:"0";s:6:"filter";s:3:"raw";}}