`ʐZ<?php exit; ?>a:1:{s:7:"content";O:8:"stdClass":24:{s:2:"ID";i:1850;s:11:"post_author";s:1:"1";s:9:"post_date";s:19:"2016-08-08 10:07:51";s:13:"post_date_gmt";s:19:"2016-08-08 02:07:51";s:12:"post_content";s:759:"亿欧消息，专注于零部件供应的电商企业工品一号宣布完成新一轮总额4000万元人民币的A轮融资。本轮融资由天使投资方顺融资本及多家关注B2B领域的投资机构完成。苏州工品一号网络科技有限公司，成立于2015年1月，坐落在苏州工业园区，是一家工业品电商公司，以紧固件作为切入点，专注标准化工业零配件行业的在线交易，平台采用自营模式，在苏州和戴南建立了自己的仓储中心，客户主要面向全国从事标准零配件贸易的中小B商户，为客户提供一站式采购平台。 <a href="http://www.iyiou.com/p/30061" target="_blank" rel="nofollow" data-stat-click="kuaixun.kuaixunlianjie.3.21485">[原文链接]</a>";s:10:"post_title";s:65:"零部件供应平台工品一号完成4000万人民币A轮融资";s:12:"post_excerpt";s:0:"";s:11:"post_status";s:7:"publish";s:14:"comment_status";s:4:"open";s:11:"ping_status";s:6:"closed";s:13:"post_password";s:0:"";s:9:"post_name";s:185:"%e9%9b%b6%e9%83%a8%e4%bb%b6%e4%be%9b%e5%ba%94%e5%b9%b3%e5%8f%b0%e5%b7%a5%e5%93%81%e4%b8%80%e5%8f%b7%e5%ae%8c%e6%88%904000%e4%b8%87%e4%ba%ba%e6%b0%91%e5%b8%81a%e8%bd%ae%e8%9e%8d%e8%b5%84";s:7:"to_ping";s:0:"";s:6:"pinged";s:0:"";s:13:"post_modified";s:19:"2016-08-08 10:07:51";s:17:"post_modified_gmt";s:19:"2016-08-08 02:07:51";s:21:"post_content_filtered";s:0:"";s:11:"post_parent";i:0;s:4:"guid";s:45:"http://qisumu.com/?post_type=news&#038;p=1850";s:10:"menu_order";i:0;s:9:"post_type";s:4:"news";s:14:"post_mime_type";s:0:"";s:13:"comment_count";s:1:"0";s:6:"filter";s:3:"raw";}}